func params1024() (*PrivateKey, error) {

	n := new(big.Int)
	n.SetString("", 10)

	nsq := new(big.Int)
	nsq.SetString("", 10)

	g := new(big.Int)
	g.SetString("", 10)

	l := new(big.Int)
	l.SetString("", 10)

	u := new(big.Int)
	u.SetString("", 10)

	return &PrivateKey{
		PublicKey: PublicKey{
			N:        n,
			NSquared: nsq,
			G:        g, // g = n + 1
		},
		L: l,
		U: u,
	}, nil
}

