	c, err := Encrypt(&privKey.PublicKey, m.Bytes())
	cs, errs := Encrypt(&privKey.PublicKey, ms.Bytes())
	if err != nil {
		println("error")
	}

	if errs != nil {
		println("error")
	}

	for n := 0; n < b.N; n++ {
		AddCipher(&privKey.PublicKey, c, cs)
	}
